mapper.select:
select i.*, (select a.name from JY_BASE_ACCOUNT a where a.id = i.createuser) as createName, (select a.name from JY_BASE_ACCOUNT a where a.id = i.checkuser) as checkName, (select o.name from JY_BASE_ORG o where i.creorgid = o.id) as orgName, (select o.name from JY_BASE_ORG o where i.checkorg = o.id) as checkOrgName from jy_pos_price_apply i where 1 = 1
AND i.status = #{param.status}
AND i.orderNo like '%'||#{param.orderNo}||'%'
AND i.createtime >= #{param.applyStartDate}
AND i.createtime <= #{param.applyEndDate}
AND i.orgid IN (SELECT jbg.id FROM jy_base_org jbg start with jbg.id=#{param.orgId} connect by prior jbg.id = jbg.pid)
order by i.createtime desc

mapper.delete:
delete from jy_pos_price_apply
where id in

    #{item}

mapper.update:
update jy_pos_price_apply set status = #{status, jdbcType=VARCHAR},
checkUser = #{checkUser, jdbcType=VARCHAR}, checkTime = sysdate, checkOrg = #{checkOrg, jdbcType=VARCHAR},
rejectcause = #{rejectCause, jdbcType=VARCHAR} where id = #{id, jdbcType=VARCHAR}
