mapper.update:
update jy_scm_print_template
   set 
       template = #{template},
       updatetime = sysdate,
       updateuser = #{updateUser}
 where id = #{id}

mapper.select:
select p.id, p.name, p.template, p.note, p.createtime as createTime, p.createuser as createUser, p.updatetime as updateTime, p.updateuser as updateUser, p.type, p.delflag as delFlag, p.orgid as orgId from jy_scm_print_template p where p.delflag='1'
and p.type = #{type}
order by p.createtime desc
