mapper.select:
	select t.*
   from JY_SCM_MOUDLE t
   where t.id = #{id}

mapper.update:
	UPDATE JY_SCM_MOUDLE
	SET   	status = #{status},
		    updateTime = #{updateTime},
          	updateUser = #{updateUser}
	WHERE id=#{id}

mapper.select:
select t.*,jsa.name,jsa.path from jy_scm_moudle t left join JY_SYS_ATTACHMENT jsa on t.id=jsa.busnessid
	where t.status='1'

		and t.type=#{type}
