mapper.select:
select p.id as id, p.code as code, p.cateid as cateId, p.catejewelryid as cateJewelryId, p.jewelrymode as jewelryMode, p.count as count, p.status as status, p.warehouseid as warehouseId, w.name as warehouseName, p.locationid as locationId, p.controltype as controlType, p.orgid as orgId, o.longname as orgName, o.name as orgSimpleName, p.costpur as costPur, p.costfin as costFin, p.costchk as costChk, p.costadd as costAdd, p.wagemode as wageMode, p.wagebasic as wageBasic, p.wagesw as wageSw, p.wageew as wageEw, p.wagepw as wagePw, p.wagecw as wageCw, p.wageow as wageOw, p.wagese as wageSe, p.costse as costSe, p.costcer as costCer, p.totalweight as totalWeight, p.goldtype as goldType, p.goldcost as goldCost, p.goldsell as goldSell, p.goldvalue as goldValue, p.goldcostlose as goldCostLose, p.goldselllose as goldSellLose, p.goldweight as goldWeight, p.labeltype as labelType, p.priceold as priceOld, p.price as price, p.pricesuggest as priceSuggest, p.pricetrade as pricetrade, p.inaccountdate as inAccountDate, p.costdate as costDate, p.rejectdate as rejectDate, p.length as length, p.width as width, p.height as height, p.circel as circel, p.franchiseeid as franchiseeId, f.name as franchiseeName, p.moucode as mouCode, p.moudtlcode as moudtlCode, md.suppmoucode as modelCode, p.name as name, p.description as description, p.costrange as costRange, p.stoneshape as stoneShape, p.stoneshaperange as stoneShapeRange, p.serialno as serialNo, p.epc as epc, p.fgoldweight as fGoldWeight, p.inwarehousedate as inWarehouseDate, p.ccategoryid as cCategoryId, p.fcategoryid as fCategoryId, p.scategoryid as sCategoryId, p.remarks as remarks, p.createtime as createTime, u.name as createUser, p.updatetime as updateTime, p.updateuser as updateUser, p.wageadd as wageAdd, p.purchasenum as purchaseNum, p.subcode as subCode, p.cernum as cerNum, p.weightrange as weightRange, p.primarycode as primarycode, p.noticeno as noticeno, p.wholesale as wholesale, p.stonesize as stonesize from jy_scm_ims_product p left join jy_base_franchisee f on p.franchiseeid = f.id left join jy_base_org o on p.orgid = o.id left join jy_scm_moudledetail md on p.moudtlcode = md.id left join jy_base_account u on u.id = p.createuser left join jy_scm_warehouse w on w.id=p.warehouseid where 1=1
AND p.orgid=#{param.orgId}
AND p.status in (#{param.status},#{param.stockStatus},#{param.skStatus})
AND p.status = #{param.status}
AND p.status in ('0','A','1','E','9','F')
AND p.status = #{param.status}
AND p.franchiseeid=#{param.franchiseeId}
AND p.goldType=#{param.goldType}
AND upper(p.code) like '%'||upper(#{param.code})||'%'
AND upper(p.inwarehousenum) like '%'||upper(#{param.inWarehouseNum})||'%'
AND p.cateId = #{param.cateId}
AND p.cateJewelryId = #{param.cateJewelryId}
order by p.status ,p.createtime desc

select p.id as id, p.code as code, p.cateid as cateId, p.catejewelryid as cateJewelryId, p.jewelrymode as jewelryMode, p.count as count, p.status as status, p.warehouseid as warehouseId, w.name as warehouseName, p.locationid as locationId, p.controltype as controlType, p.orgid as orgId, o.longname as orgName, o.name as orgSimpleName, p.costpur as costPur, p.costfin as costFin, p.costchk as costChk, p.costadd as costAdd, p.wagemode as wageMode, p.wagebasic as wageBasic, p.wagesw as wageSw, p.wageew as wageEw, p.wagepw as wagePw, p.wagecw as wageCw, p.wageow as wageOw, p.wagese as wageSe, p.costse as costSe, p.costcer as costCer, p.totalweight as totalWeight, p.goldtype as goldType, p.goldcost as goldCost, p.goldsell as goldSell, p.goldvalue as goldValue, p.goldcostlose as goldCostLose, p.goldselllose as goldSellLose, p.goldweight as goldWeight, p.labeltype as labelType, p.priceold as priceOld, p.price as price, p.pricesuggest as priceSuggest, p.pricetrade as pricetrade, p.inwarehousedate as inWarehouseDate, p.inaccountdate as inAccountDate, p.costdate as costDate, p.rejectdate as rejectDate, p.length as length, p.width as width, p.height as height, p.circel as circel, p.franchiseeid as franchiseeId, f.name as franchiseeName, p.moucode as mouCode, p.moudtlcode as moudtlCode, md.suppmoucode as modelCode, p.name as name, p.description as description, p.costrange as costRange, p.stoneshape as stoneShape, p.stoneshaperange as stoneShapeRange, p.serialno as serialNo, p.epc as epc, p.fgoldweight as fGoldWeight, p.inwarehousenum as inWarehouseNum, p.ccategoryid as cCategoryId, p.fcategoryid as fCategoryId, p.scategoryid as sCategoryId, p.remarks as remarks, p.createtime as createTime, p.createuser as createUser, p.updatetime as updateTime, p.updateuser as updateUser, p.wageadd as wageAdd, p.purchasenum as purchaseNum, p.subcode as subCode, p.cernum as cerNum, p.weightrange as weightRange, p.primarycode as primarycode, p.noticeno as noticeno, p.wholesale as wholesale, p.stonesize as stonesize from jy_scm_ims_product p left join jy_base_franchisee f on p.franchiseeid = f.id left join jy_base_org o on p.orgid = o.id left join jy_scm_moudledetail md on p.moudtlcode = md.id left join jy_scm_warehouse w on w.id=p.warehouseid where 1=1
and p.id=#{id}
and p.code=#{code}
and p.orgId=#{orgId}
and p.status=#{status}

