mapper.select:
	select jo.PRODUCTID
   from JY_SCM_PUR_ORDERSPLIT jo
   where jo.status='0' and jo.orderDetailId = #{orderDetailId}

mapper.delete:
DELETE FROM JY_SCM_PUR_ORDERDETAIL WHERE id in(

		#{item.id}

	)

DELETE FROM JY_SCM_PUR_ORDERDETAIL WHERE orderId in(

		#{item.orderId}

	)

mapper.select:
select t.mdCode, m.code as mdCodeName, md.id as mdtlCode, md.suppmoucode as mdtlCodeName, f.id as franchiseeId, f.name as franchiseeName, t.gMaterial, t.gWeight, t.dWeight, t.dClarity, t.dColor, t.weight, t.feeType, t.status, t.basicCost, t.additionCost, t.otherCost, t.unitprice, t.totalFee, t.description, t.id as beforeId, t.circel, t.cut, (select count(1) from jy_scm_ims_product p inner join jy_scm_moudle m on m.code = p.moucode where m.id = t.mdCode and p.status = 'B' and p.orgId in (select o.id from jy_base_org o where o.companyid = '1' and o.orggrade != '4')) as stockNum, (t.numbers - (select count(1) from jy_scm_pur_ordersplit t1 where t1.orderdetailid = t.id)) as numbers from jy_scm_pur_orderdetail t left join jy_scm_moudle m on t.mdcode = m.id left join jy_scm_moudledetail md on md.moudleid=m.id and md.majorflag='1' and rownum = 1 left join jy_base_franchisee f on f.id=md.suppliercode where t.orderid in (select id from jy_scm_pur_order o where o.ordertype = #{orderType} and o.status='2' and o.label='2'
)
and substr(m.code,0,1) =#{term}
and substr(m.code,2,1)=#{cate}
and substr(m.code,3,2)=#{group}
and t.status='1'

select t.mdCode, t.mdtlCode, t.gMaterial, t.gWeight, t.dWeight, t.dClarity, t.dColor, t.weight, t.feeType, t.status, t.basicCost, t.additionCost, t.otherCost, t.unitprice, t.totalFee, t.description, t.id as beforeId, (t.numbers - (select count(1) from jy_scm_pur_ordersplit t1 where t1.orderdetailid = t.id)) as numbers from jy_scm_pur_orderdetail t where t.orderid in
#{item.id}
and t.status='1'

mapper.update:
    	update jy_scm_pur_orderdetail
	        set 
