mapper.select:
    	select  *  from jy_scm_ims_outbound o where 1=1

		     and o.id=#{id,jdbcType=VARCHAR}


		     and o.outboundno= #{outBoundNo,jdbcType=VARCHAR}

select d.*,(select name from jy_scm_warehouse where id=d.outWarehouseId) as outWarehouseName,(select name from jy_scm_warehouse_location where id=d.outLocationId) as outLocationName ,m.price as unitPrice,m.type as feeType,t.cernum as cerno from JY_SCM_IMS_OUTBOUNDDETAIL d left join jy_scm_ims_material m on m.code=d.code left join jy_scm_ims_product t on t.code=d.code where 1=1
and d.id=#{id,jdbcType=VARCHAR}
and d.outboundid= #{outBoundId,jdbcType=VARCHAR}

select o.id, o.outboundno as outBoundNo, o.type, o.status, (select sum(num) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as num, (select sum(weight) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as weight, o.orgid as orgId, (select name from jy_base_org where id=o.orgid) as orgName, o.inorgid as inOrgId, (select name from jy_base_org where id=o.inorgid) as inOrgName, o.outorgid as outOrgId, (select name from jy_base_org where id=o.outorgid) as outOrgName, o.ordernum as orderNum, o.createtime as createTime, o.deletetag as deleteTag, (select sum(costing) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as totalCosing, (select sum(price) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as totalPrice, (select sum(pradeprice) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as totalPradeprice, (select sum(wage) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as totalWage, (select sum(tageprice) from jy_scm_ims_outbounddetail where outboundno=o.outboundno) as totalTageprice from jy_scm_ims_outbound o where 1=1
and upper(o.outboundno) like '%'||upper(#{param.outBoundNo,jdbcType=VARCHAR})||'%'
and upper(o.ordernum) like '%'||upper(#{param.orderNum,jdbcType=VARCHAR})||'%'
and o.inorgid=#{param.inOrgId,jdbcType=VARCHAR}
and o.outorgid=#{param.outOrgId,jdbcType=VARCHAR}
AND o.createtime >= #{param.createTime,jdbcType=VARCHAR}
AND o.createtime <= #{param.createTimeEnd,jdbcType=VARCHAR}
AND o.catgory = #{param.catgory,jdbcType=VARCHAR}
and o.status=#{param.status,jdbcType=VARCHAR}
and o.status in ('2','3')
and o.orgid=#{param.orgId,jdbcType=VARCHAR}
order by o.createtime desc


	   select t.name as name,t.code as code,t.cernum as cerno,t.remarks as proRemarks,t.count as num,t.totalweight as weight
      from jy_scm_ims_product t
       where upper(t.code) =#{code,jdbcType=VARCHAR}
		   AND t.status = 'B'
	   and t.orgid=#{orgid,jdbcType=VARCHAR}

	   select t.name as name,t.code as code,t.cernum as cerno,t.remarks as proRemarks,t.count as num,t.totalweight as weight
      from jy_scm_ims_product t
       where upper(t.code) =#{code,jdbcType=VARCHAR} and t.orgid=#{orgid,jdbcType=VARCHAR}

mapper.delete:
   		delete from JY_SCM_IMS_OUTBOUNDDETAIL
   		where id in

		    #{item.id,jdbcType=VARCHAR}

mapper.select:
	select * from  jy_scm_ims_outbounddetail  
	where id not in

	    #{item.id,jdbcType=VARCHAR}

    and outBoundNo=#{outBoundNo,jdbcType=VARCHAR}
