mapper.select:
select f.id as id, f.transferno as transferno,f.type as type,a.name as createUser,c.name as checkUser,f.createtime as createTime, f.updatetime as updateTime, f.status as status ,f.checktime as checkTime,f.catgory as catgory,h.name as inWarehouseId,l.name as inLocationId, (select sum(d.num) from jy_scm_ims_transferdetail d where f.id=d.TRANSFERID) as num, (select sum(d.weight) from jy_scm_ims_transferdetail d where f.id=d.TRANSFERID) as weight,(select orgid from jy_base_account t where t.id=f.createuser) as orgId from jy_scm_ims_transfer f left join jy_base_account a on a.id=f.createuser left join jy_base_account c on c.id=f.checkuser left join jy_scm_warehouse h on h.id=f.inWarehouseId left join jy_scm_warehouse_location l on l.id=f.inLocationId where 1=1
AND F.STATUS=#{param.status,jdbcType=VARCHAR}
AND F.catgory=#{param.catgory,jdbcType=VARCHAR}
AND upper(F.transfernoï¼‰ like '%'||upper(#{param.transferNo})||'%'
ORDER BY F.CREATETIME asc
