mapper.select:
select t.*,(select jsw.name from jy_scm_warehouse jsw where jsw.id=t.warehouseid) as warehouseName from jy_scm_warehouse_location t where 1=1
AND t.status=#{param.status}
AND t.warehouseid=#{param.warehouseid}
ORDER BY t.createTime desc

	select *
   from jy_scm_warehouse_location
    where 1=1

    	AND id = #{id}


    	AND warehouseid=#{warehouseid}

   order by sort asc

mapper.update:
UPDATE jy_scm_warehouse_location
SET   status = #{status},
	  updatetime =#{updateTime},
	  updateuser = #{updateUser},
	  updateName = #{updateName}
WHERE id=#{id}
