mapper.select:
select u.id as accountId, u.loginName as loginName, r.id as roleId, r.name as roleName, u.orgid as orgId, u.orgname as orgName, o.name as company, o.distcode as distCode, u.name as name, u.userno as userNo, u.picUrl as picUrl, u.email as email, u.type as type, u.sex as sex, u.mobile as mobile, u.address as address, u.emercontractor as emercontractor, u.emercontractnm as emercontractNm, u.rank as rank, u.grade as grade, u.isvalid as isvalid, to_char(u.birthdaty,'yyyy/mm/dd') as birthdaty, u.createtime as createTime, u.createuser as createUser, u.createname as createName, u.updatetime as updateTime, u.skin as skin, u.description as description from jy_base_account u left join jy_base_ACCOUNT_ROLE ur on u.id = ur.userid left join jy_base_role r on ur.roleid = r.id left join jy_base_org o on o.id=u.company WHERE 1=1
and u.loginName=#{loginName}
and u.id=#{accountId}

mapper.delete:
DELETE FROM jy_base_account WHERE id in(
	
		#{o.accountId}
	
	)

mapper.select:
	
   SELECT a.* FROM jy_base_account a WHERE 1=1 
   AND a.isvalid='1' 
   AND a.loginname=#{loginName,jdbcType=VARCHAR}

mapper.update:
UPDATE jy_base_account 
SET   skin =#{skin}
WHERE id=#{accountId}

UPDATE jy_base_account 
SET   name =#{name},
	  email =#{email},
	  updateTime =#{updateTime}
WHERE id=#{accountId}

UPDATE jy_base_account 
SET   picUrl =#{picUrl},
	  updateTime =#{updateTime}
WHERE id=#{accountId}

mapper.select:
SELECT COUNT(*)
		  FROM jy_base_account t 
WHERE  t.loginName=#{loginName}

mapper.update:
UPDATE jy_base_account
SET   password =#{password},
	  salt =#{salt}
WHERE id=#{accountId}

UPDATE jy_base_account
SET   isvalid =#{isValid},
	  updatetime =#{updateTime},
	  updateuser = #{updateUser},
	  updateName = #{updateName}
WHERE id=#{accountId}
