mapper.select:
select p.id, p.code, p.PRIMARYCODE as primaryCode, p.name, p.warehouseid as outWarehouseId, p.locationid as outLocationId, p.count as num, p.totalweight as weight, p.wagemode as feeType, p.orgid as orgId from jy_scm_ims_product p
where p.code=#{codeVO.code}
where p.PRIMARYCODE=#{codeVO.code}
and p.orgid=#{codeVO.orgId}
